\r\ntryExcept = ''''''\r\n	try:\r\n		a = d[''doesntexist'']\r\n	except KeyError:\r\n		pass\r\n''''''\r\n\r\ngetIfElse = ''''''\r\n	a = d.get(''doesntexist'', None)\r\n	if a == None:\r\n		pass\r\n	else:\r\n		pass\r\n''''''\r\n\r\nresults = {\r\n	''tryExcept'' : timeit.Timer(tryExcept, setup).timeit(100000),\r\n	''getIfElse'' : timeit.Timer(getIfElse, setup).timeit(100000)\r\n}\r\n\r\nfor k, v in results.items():\r\n	print ''%20s => %fs'' % (k, v)\r\n</pre>', 'The Cost of Except in Python', 0, '', 'inherit', 'open', 'open', '', '1052-revision-2', '', '', '2012-01-06 12:12:26', '2012-01-06 19:12:26', '', 1052, 'http://dan.lecocq.us/wordpress/2012/01/06/1052-revision-2/', 0, 'revision', '', 0),
